package main

import (
	"encoding/base64"
	"fmt"
	"testing"
)



func Test_B64(t *testing.T) {
	//密文1484 第一次decode后 1112 第二次decode后832 原文 827
	origin := ""
	fmt.Println("origin-len:", len(origin))
	b1, _ := base64.StdEncoding.DecodeString(origin)
	fmt.Println("b1-len:", len(b1))

	b1Byte := []byte(b1)
	fmt.Println("b1byte.len:", len(b1Byte))
	b2, _ := base64.StdEncoding.DecodeString(string(b1Byte))	
	fmt.Println("b2.len:", len(b2))
}